import {createSlice} from "@reduxjs/toolkit"

const cartSlice =createSlice ({
    name: "venkat",
    initialState :{
      user: "kosurivenkat9",
    },
    reducers : {
      update : (state,action) => {
          state.name =action.playload.name ;
          state.user =action.playload.user
      }
    }
})

export const {update} = cartSlice.actions;
export default cartSlice.reducer
