import React, {useState} from 'react'
import {Addtocart} from './carSlice'
import {Removetocart} from './carSlice'

const Todo = () => {
  const name = useSelector((state) => state.counter)
   const [counter,setcounter]= useState(name.value)
   const [count,setcount] =useState(name.value)
   const dispatch = useDispatch()
   const addtodo = () => {
       dispatch(Addtocart())
     setcounter(name.value)
   }
   const removetodo = () => {
    dispatch(Removetocart())
    setcount(name.value)
   }
    return (
        <div>
          <p>{counter}</p>
            <button onClick={addtodo}>increment</button> 
            <button onClick={removetodo}>decrement</button>
            <p>{count}</p>
        </div>
    )
}

export default Todo
