import React,{useSelector,useDispatch,useState} from 'react'
import {Addtocart} from './carSlice'

const Todo = () => {
  const name = useSelector((state) => state.counter)
   const [counter,setcounter]= useState(name.value)
   const dispatch = useDispatch()
   const addtodo = () => {
       dispatch(Addtocart())
     setcounter(name.value)
   }
    return (
        <div>
          <p>{counter}</p>
            <button onclick={addtodo}>click me </button>
        </div>
    )
}

export default Todo
