import {createSlice} from '@reduxjs/toolkit'

const carSlice = createSlice({
    name: "counter",
    initialState : {
        value:0,
    },
    reducers: {
      Addtocart (state) {
        state.value += 1;
      },
      removetocart (state) {
          state.value +=1 ;
      },

    },
})

export const {Addtocart, Removetocart} = carSlice.actions

export default carSlice.reducer
